require('./helper');

var Item = require('./model');
var app = require('./app');
var assert = require('assert');
var request = require('supertest');

describe('Filtering', function () {
  beforeEach(function (done) {
    Item
      .create({active: true})
      .then(function () {
        Item
          .create({active: false})
          .then(function () {
            done();
          });
      });
  });

  describe('with active=true parameter', function () {
    it('filters items by active state', function (done) {
      request(app)
        .get('/?active=true')
        .expect(function (res) {
          assert.equal(1, res.body.length);
        })
        .end(done);
    });
  });
});
