require('./helper');

var Item = require('./model');
var app = require('./app');
var assert = require('assert');
var request = require('supertest');

describe('Sorting', function () {
  beforeEach(function (done) {
    Item
      .create({name: 'Item 1'})
      .then(function () {
        Item
          .create({name: 'Item 2'})
          .then(function () {
            done();
          });
      });
  });

  describe('with sort=name parameter', function () {
    it('sorts item ascending by name', function (done) {
      request(app)
        .get('/?sort=name')
        .expect(function (res) {
          assert.equal('Item 1', res.body[0].name);
        })
        .end(done);
    });
  });

  describe('with sort=-name parameter', function () {
    it('sorts item descending by name', function (done) {
      request(app)
        .get('/?sort=-name')
        .expect(function (res) {
          assert.equal('Item 2', res.body[0].name);
        })
        .end(done);
    });
  });
});
